  channel: "",
};

const onSubmit = (values) => {
  console.log(values);
};

const validate = (values) => {
  const errors = {};

  if (!values.name) {
    errors.name = "필수 기입사항입니다";
  }

  if (!values.email) {
    errors.email = "필수 기입사항입니다";
  }

  if (!values.channel) {
    errors.channel = "필수 기입사항입니다";
  }

  return errors;
};

export default SignupForm;
