import React from "react";
import { useFormik } from "formik";
import styled from "styled-components";

const initialValues = {
  name: "",
  email: "",
  channel: "",
};

const onSubmit = (values) => {
  console.log(values);
};

const validate = (values) => {
  const errors = {};

  if (!values.name) {
    errors.name = "필수 기입사항입니다";
  }

  if (!values.email) {
    errors.email = "필수 기입사항입니다";
  }

  if (!values.channel) {
    errors.channel = "필수 기입사항입니다";
  }

  return errors;
};

//style

const ValidElert = styled.div`
  color: red;
`;
const Form = styled.form`
  w
`;

// jsx

const SignupForm = () => {
  const formik = useFormik({
    initialValues,
    validate,
    onSubmit,
  });

  return (
    <div>
      <form onSubmit={formik.handleSubmit}>
        <label>name</label>
        <input
          type="text"
          id="name"
          name="name"
          onChange={formik.handleChange}
          value={formik.values.name}
        ></input>
        <ValidElert>{formik.errors.name}</ValidElert>

        <label>E-mail</label>
        <input
          type="text"
          id="email"
          name="email"
          onChange={formik.handleChange}
          value={formik.values.email}
        ></input>
        <div>{formik.errors.email}</div>

        <label>channel</label>
        <input
          type="text"
          id="channel"
          name="channel"
          onChange={formik.handleChange}
          value={formik.values.channel}
        ></input>
        <div>{formik.errors.channel}</div>

        <button type="submit">확인</button>
      </form>
    </div>
  );
};

export default SignupForm;
