import React from "react";
import { useFormik } from "formik";
const validate = (values) => {
  const errors = {};

  if (!values.email) {
    errors.email = "이메일을 입력해주세요."; //아무것도 입력하지 않았을 때
  } else if (!/^[A-Z0-9._%+-]+@[A-Z0-9.-]+\.[A-Z]{2,4}$/i.test(values.email)) {
    errors.email = "이메일 형식이 아닙니다."; // 조건의 형식이 아닐 때
  }

  if (!values.password) {
    errors.password = "Required"; // 아무것도 입력하지 않았을 때
  }

  return errors;
};
const SignupForm = () => {
  const initialValues = {
    email: "",
    password: "",
  };
  const formik = useFormik({
    initialValues: initialValues,
    validate: validate,
    onSubmit: (values) => console.log(values),
  });

  return (
    //마크업
    <>
      <label>회원가입 양식</label>
      <form onSubmit={formik.handleSubmit}>
        <label>email</label>
        <input
          type="text"
          name="email"
          value={formik.values.email}
          onChange={formik.handleChange}
        />
        <div>
          <label style={{ color: "red" }}>{formik.errors.email}</label>
        </div>
        <label>password</label>
        <input
          type="password"
          name="password"
          value={formik.values.password}
          onChange={formik.handleChange}
        />
        <div>
          <label style={{ color: "red" }}>{formik.errors.password}</label>
        </div>
        <button type="submit">전송</button>
      </form>
    </>
  );
};

export default SignupForm;
