import React from "react";
import { useFormik } from "formik";

const initialValues = {}

const SignupForm = () => {
  const formik = useFormik({
    //jsx의  input name property와 일치시켜야 함
    initialValues: {
      name: "",
      email: "",
      channel: "",
    },
    onSubmit: (values) => {
      console.log(formik.values);
    },
    validate: (values) => {
      const errors = {};
      if (!values.name) {
        errors.name = "필수 기입사항입니다";
      }
      if (!values.email) {
        errors.email = "필수 기입사항입니다";
      }
      if (!values.channel) {
        errors.channel = "필수 기입사항입니다";
      }
      return errors;
    },
  });

  // console.log(formik.values);

  return (
    <div>
      <form onSubmit={formik.handleSubmit}>
        <label>name</label>
        <input
          type="text"
          id="name"
          name="name"
          onChange={formik.handleChange}
          value={formik.values.name}
        ></input>

        <label>E-mail</label>
        <input
          type="text"
          id="email"
          name="email"
          onChange={formik.handleChange}
          value={formik.values.email}
        ></input>

        <label>channel</label>
        <input
          type="text"
          id="channel"
          name="channel"
          onChange={formik.handleChange}
          value={formik.values.channel}
        ></input>

        <button type="submit">Submit</button>
      </form>
    </div>
  );
};

export default SignupForm;
