import React from "react";
import { useFormik } from "formik";
import styled from "styled-components";

/* 

formik tutorial 

1. initialValues 생성
  initialValues 객체에 저장된 key, value 는 formik의 value 객체로 저장됨
  즉, Values는 formik.values.key 임

  initialValues객체의 키는 jsx의 input의 name 속성과 일치 시켜야 함


2. valiedate 로직 생성
  valiedate()는 파라미터로 fromik.value를 가져와야 함




*/
const initialValues = {
  name: "",
  email: "",
  channel: "",
};

// input의 values를 객체로 반환함
const onSubmit = (values) => {
  console.log(values);
};

const validate = (values) => {
  const errors = {};

  if (!values.name) {
    errors.name = "필수";
  }

  if (!values.email) {
    errors.email = "필수";
  }

  if (!values.channel) {
    errors.channel = "필수";
  }

  return errors;
};

const SignupForm = () => {
  const formik = useFormik({
    initialValues,
    validate,
    onSubmit,
  });

  return (
    <Conatainer>
      <Form onSubmit={formik.handleSubmit}>
        <Label>이름</Label>
        <ContentBx>
          <Input
            type="text"
            id="name"
            name="name"
            onChange={formik.handleChange}
            value={formik.values.name}
          ></Input>
          <ValidElert>{formik.errors.name}</ValidElert>
        </ContentBx>

        <Label>이메일</Label>
        <ContentBx>
          <Input
            type="text"
            id="email"
            name="email"
            onChange={formik.handleChange}
            value={formik.values.email}
          ></Input>
          <ValidElert>{formik.errors.email}</ValidElert>
        </ContentBx>

        <Label>유입경로</Label>
        <ContentBx>
          <Input
            type="text"
            id="channel"
            name="channel"
            onChange={formik.handleChange}
            value={formik.values.channel}
          ></Input>
          <ValidElert>{formik.errors.channel}</ValidElert>
        </ContentBx>
        <Submitbtn type="submit">확인</Submitbtn>
      </Form>
    </Conatainer>
  );
};

//style
const Conatainer = styled.div`
  height: 100vh;
  display: flex;
  align-items: center;
  justify-content: center;
  border: 1px solid 
`;
const Label = styled.label`
  display: block;
`;

const ContentBx = styled.div`
  display: flex;
  align-items: center;
`;

const ValidElert = styled.div`
  margin-left: 30px;
  color: red;
`;
const Form = styled.form`
  width: 500px;
`;

const Input = styled.input`
  width: 400px;
  height: 40px;
  border: 1px solid #ddd;
  border-radius: 10px;
  margin: 10px 0;
`;

const Submitbtn = styled.button`
  margin-top: 100px;
  width: 100%;
  border: none;
  height: 40px;
`;

export default SignupForm;
