import React from "react";
import { useFormik } from "formik";
// import { object, string, number } from "yup";

// const validationSchema = object().shape({
//   email: string().email("이메일 형식에 맞춰주세요.").required("입력해주세요."),
//   password: string().required("비밀번호를 입력해주세요."),
//   age: number().required("입력해주세요"),
// });

const validate = (values) => {
  const errors = {};
  if (!values.email) {
    errors.email = "Required";
  } else if (!/^[A-Z0-9._%+-]+@[A-Z0-9.-]+\.[A-Z]{2,4}$/i.test(values.email)) {
    errors.email = "Invalid email address";
  }

  if (!values.password) {
    errors.password = "Required";
  }

  return errors;
};

export const SignupForm = () => {
  const initialValues = {
    isemail: "",
    password: "",
  };
  const formik = useFormik({
    initialValues,
    validate,
    onSubmit: (values) => console.log(values),
  });

  return (
    <>
      <label>회원가입 양식</label>
      <form onSubmit={formik.handleSubmit}>
        <label>email</label>
        <input
          type="text"
          name="email"
          value={formik.values.email}
          onChange={formik.handleChange}
        />
        <div>
          <label style={{ color: "red" }}>{formik.errors.email}</label>
        </div>
        <label>password</label>
        <input
          type="password"
          name="password"
          value={formik.values.password}
          onChange={formik.handleChange}
        />
        <div>
          <label style={{ color: "red" }}>{formik.errors.password}</label>
        </div>
        <button type="submit">전송</button>
      </form>
    </>
  );
};
